    private void btnDiagonalInvertidaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnDiagonalInvertidaMouseClicked
        // Diagonal invertida
        DefaultTableModel model2 = (DefaultTableModel) jTable1.getModel();

        arregloAux = objeto.obtenerDiagonalInvertida();
        model2.setRowCount(1);
        model2.setColumnCount(arregloAux.length);

        for(int i=0; i<arregloAux.length; i++){
            jTable1.setValueAt(arregloAux[i], 0, i);
        }
    }//GEN-LAST:event_btnDiagonalInvertidaMouseClicked

