    private void btnGenerarMatrizMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnGenerarMatrizMouseClicked

        try{
            tamaño = Integer.parseInt(jTextField1.getText());
        }catch(NumberFormatException e){
            JOptionPane.showMessageDialog(this, "ERROR: "+e);
        }
        objeto = new Matriz(tamaño);
        mostrarMatriz(objeto.getMatriz());
    }//GEN-LAST:event_btnGenerarMatrizMouseClicked

